import React, { useState } from "react";
import "./app.css";

function App() {
  const [selectedOption, setSelectedOption] = useState(null);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");

  const moodOptions = [
    "I'm feeling sad",
    "I need motivation",
    "I feel anxious",
    "I just want to talk",
  ];

  const handleOptionSelect = (option) => {
    setSelectedOption(option);
    setMessages([{ sender: "user", text: option }]);
    // You can send this initial message to server if you want
  };

  const handleSend = async () => {
    if (!input.trim()) return;

    const newMessages = [...messages, { sender: "user", text: input }];
    setMessages(newMessages);

    try {
      const response = await fetch("http://localhost:5000/api/chat", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ message: input }),
      });

      const data = await response.json();

      setMessages([...newMessages, { sender: "bot", text: data.response }]);
    } catch (error) {
      setMessages([
        ...newMessages,
        { sender: "bot", text: "Error: Failed to get response from OpenAI" },
      ]);
    }

    setInput("");
  };

  return (
    <div className="chat-container">
      <h2>AI Mental Health Companion</h2>

      {!selectedOption ? (
        <div className="options-box">
          <h4>Select how you feel to start chatting:</h4>
          {moodOptions.map((option, index) => (
            <button key={index} onClick={() => handleOptionSelect(option)}>
              {option}
            </button>
          ))}
        </div>
      ) : (
        <>
          <div className="chat-box">
            {messages.map((msg, index) => (
              <div
                key={index}
                className={`chat-message ${msg.sender === "user" ? "user" : "bot"}`}
              >
                <strong>{msg.sender === "user" ? "You" : "AI"}:</strong> {msg.text}
              </div>
            ))}
          </div>

          <div className="input-box">
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="Type a message..."
            />
            <button onClick={handleSend}>Send</button>
          </div>
        </>
      )}
    </div>
  );
}

export default App;
