import React, { useState } from "react";
import "./App.css";

function App() {
  const [selectedOption, setSelectedOption] = useState(null);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [username, setUsername] = useState("");
  const [loggedIn, setLoggedIn] = useState(false);

  const moodOptions = ["motivation", "anxiety", "talk"];

  const handleOptionSelect = (option) => {
    setSelectedOption(option);
    setMessages([{ sender: "user", text: `I feel: ${option}` }]);
  };

  const handleSend = async () => {
    if (!input.trim()) return;

    const newMessages = [...messages, { sender: "user", text: input }];
    setMessages(newMessages);

    try {
      const response = await fetch("/api/chat", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          option: selectedOption.trim().toLowerCase(),
          message: input,
          username,
        }),
      });

      const data = await response.json();
      setMessages([...newMessages, { sender: "bot", text: data.response }]);
    } catch (error) {
      setMessages([
        ...newMessages,
        { sender: "bot", text: "Error: Failed to get response from server" },
      ]);
    }

    setInput("");
  };

  if (!loggedIn) {
    return (
      <div className="chat-container">
        <h2>AI Mental Health Companion</h2>
        <div className="login-box">
          <input
            type="text"
            placeholder="Enter your name to start"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <button onClick={() => username.trim() && setLoggedIn(true)}>
            Start Chat
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="chat-container">
      <h2>Hi {username}, how are you feeling?</h2>

      {!selectedOption ? (
        <div className="options-box">
          <h4>Select how you feel to start chatting:</h4>
          {moodOptions.map((option, index) => (
            <button key={index} onClick={() => handleOptionSelect(option)}>
              {option}
            </button>
          ))}
        </div>
      ) : (
        <>
          <div className="chat-box">
            {messages.map((msg, index) => (
              <div
                key={index}
                className={`chat-message ${
                  msg.sender === "user" ? "user" : "bot"
                }`}
              >
                <strong>{msg.sender === "user" ? "You" : "AI"}:</strong>{" "}
                {msg.text}
              </div>
            ))}
          </div>

          <div className="input-box">
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="Type a message..."
            />
            <button onClick={handleSend}>Send</button>
          </div>
        </>
      )}
    </div>
  );
}

export default App;
