// src/App.js
import React, { useState } from 'react';
import './App.css';

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');

  const options = [
    'I am feeling sad',
    'I feel anxious',
    'I need motivation',
    'I am lonely',
  ];

  const handleSend = async (message) => {
    const userMsg = message || input;
    if (!userMsg) return;

    const newMessages = [...messages, { sender: 'user', text: userMsg }];
    setMessages(newMessages);
    setInput('');

    try {
      const res = await fetch('http://localhost:5000/api/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: userMsg }),
      });
      const data = await res.json();

      setMessages([...newMessages, { sender: 'ai', text: data.response }]);
    } catch (err) {
      setMessages([...newMessages, { sender: 'ai', text: 'Error: Failed to get response from AI' }]);
    }
  };

  return (
    <div className="app">
      <h1>AI Mental Health Companion</h1>

      <div className="options">
        {options.map((opt, index) => (
          <button key={index} onClick={() => handleSend(opt)}>
            {opt}
          </button>
        ))}
      </div>

      <div className="chat-box">
        {messages.map((msg, index) => (
          <div key={index} className={msg.sender === 'user' ? 'user-message' : 'ai-message'}>
            {msg.text}
          </div>
        ))}
      </div>

      <div className="input-area">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type your message..."
        />
        <button onClick={() => handleSend()}>Send</button>
      </div>
    </div>
  );
}

export default App;
